@testable import Mediator
import XCTest

final class MediatorServiceTests: XCTestCase {
    var service: MediatorService!

    override func setUpWithError() throws {
        try super.setUpWithError()
        service = MediatorService()
    }

    func test_discover_path() {
        XCTAssertTrue(MediatorRouter.discover.method == .GET)
        XCTAssertTrue(MediatorRouter.discover.url.relativePath == "/.well-known/agent-configuration")
    }

    func test_set_service_url() {
        XCTAssertTrue(Networking.hostURL.absoluteString == "https://mediator.dev.essid-demo.com")
        try? service.setServiceURL(string: "https://ssi-mediator-20.esatus.com")
        XCTAssertTrue(Networking.hostURL.absoluteString == "https://ssi-mediator-20.esatus.com")
        service = MediatorService(urlString: "https://mediator.dev.essid-demo.com")
        XCTAssertTrue(Networking.hostURL.absoluteString == "https://mediator.dev.essid-demo.com")
    }

    func test_set_service_url_throws() {
        XCTAssertThrowsError(try service.setServiceURL(string: ""))
    }
}

